public class GuessingGame {    private int number;        public GuessingGame() {        number = 1 + (int)(Math.random() * 100);    }        public String guess(int N) {        if (N == number)        	return "Your guess " + N + " is just right. You guessed it";        else if (N < number)        	return "Your guess " + N + " is too low";        else        	return "Your guess " + N + " is too high";        	    }}