import java.io.*;public class KeyboardReader extends BufferedReader {    public KeyboardReader() {	super(new InputStreamReader(System.in));    }        private String readKeyboard() {        String line = "";	try {            line = this.readLine();	} catch (IOException e) {	    e.printStackTrace();	}	return line;    }    public String getInput() {         return readKeyboard();    }        public int getInteger() {        return Integer.parseInt(readKeyboard());    }}