//import javax.swing.*;import java.awt.*;import java.awt.event.*;import java.applet.*;public class SimpleGUI extends Panel implements ActionListener {	private TextArea display;	private TextField inField;	private Button goButton;	private GuessingGame game;	    public SimpleGUI(Applet a) {        createInterface();        a.add(this); 		game = new GuessingGame();      }        public SimpleGUI(Frame f) {    	createInterface();    	f.add(this);  		game = new GuessingGame();      }        private void createInterface() {    	setLayout(new FlowLayout());        display = new TextArea(10,30);        inField = new TextField(10);        inField.addActionListener(this);        goButton = new Button("Guess!");        goButton.addActionListener(this);        add(new Label("Input you guess here: "));        add(inField);        add(display);        add(goButton);            }        public void actionPerformed(ActionEvent e){        int myguess = Integer.parseInt(inField.getText());    		    display.append(game.guess(myguess) + "\n");    }}