import java.applet.Applet;  //This class uses the Applet  classimport java.awt.Graphics;   // and the Graphics classimport java.awt.Color;public class RiddleApplet extends Applet {        Riddle riddle1 =         new Riddle("What other word uses the same letters as ALGORITHM?","LOGARITHM");         public void paint(Graphics g){              g.setColor(Color.cyan);  // Code demonstrating some drawing commands              g.fillRect(20, 75, 360, 150);              g.setColor(Color.blue);              g.drawRect(20, 75, 360, 150);              g.drawString("Riddle:",40,100);              g.drawString("Answer:",40,150);              g.setColor(Color.black);              g.drawString(riddle1.getQuestion(),40,125);              g.drawString(riddle1.getAnswer(),40,175);         } //paint()} // RiddleApplet class