/*	Trivial application that displays a string - 4/96 PNL*/import java.io.*;public class TrivialApplication {    private KeyboardReader cmdline;    private GuessingGame game;	    public static void main(String args[]) {        TrivialApplication app = new TrivialApplication();        app.init();        app.play();    }	    public void init() {        cmdline = new KeyboardReader();        game = new GuessingGame();	    }	    public void play() {        int myguess;        String result;        System.out.println("Hi. I'm thinking of a secret number between 1 and 100");        System.out.println("Try to guess it.");	do {            System.out.println("Type a guess or type -1 to stop the game");            myguess = cmdline.getInteger();            if (myguess != -1)                 System.out.println(game.guess(myguess));        } while (myguess != -1);        System.out.println("That was fun. Bye!");       }}