import javax.swing.*;import java.awt.*;import java.awt.event.*;import java.applet.*;public class SimpleGUI extends JFrame implements ActionListener {	private JTextArea display;	private JTextField inField;	private JButton goButton;	private Greeter greeter;	        public SimpleGUI(String title) {    	buildGUI(); 	    greeter = new Greeter();   	    setTitle(title); 	    pack(); 	    show();    }            private void buildGUI() {        Container cPane = getContentPane();        display = new JTextArea(10,30);        inField = new JTextField(10);        inField.addActionListener(this);        goButton = new JButton("Click here for a greeting!");        goButton.addActionListener(this);        JPanel p = new JPanel();        p.add(new JLabel("Input your name here: "));        p.add(inField);        cPane.add("South",goButton);        cPane.add("Center",display);        cPane.add("North",p);          }        public void actionPerformed(ActionEvent e) {        String name = inField.getText();     	display.append(greeter.greet(name) + "\n");    }    }